--donor info
CREATE OR REPLACE VIEW vw_donor_info 
AS
SELECT 
	d.Donor_ID
,	d.Donor_Name_First
,	d.Donor_Name_Middle
,	d.Donor_Name_Last
,	D.Donor_Name_Full
,	d.Donor_Email
,	d.Donor_RSA_Identity_Number
,	d.Donor_Cell_No
,	d.Donor_Address
,	g.Donor_Gender_Name
,	b.Donor_Blood_Type_Name
,	s.Donor_Deferral_Sts_Name
FROM 
	DONOR d
	JOIN DONOR_GENDER g ON d.Donor_Gender_ID = g.Donor_Gender_ID
	JOIN DONOR_BLOOD_TYPE b ON d.Donor_Blood_Type_ID = b.Donor_Blood_Type_ID
	JOIN DONOR_DEFERRAL_STATUS s ON d.Deferral_Status_ID = s.Deferral_Status_ID;
    
    --staff 

CREATE OR REPLACE VIEW vw_staff_management_current
AS
SELECT DISTINCT 
	s.Staff_ID
,	s.Staff_Name_First
,	s.Staff_Name_Last
,	s.Staff_Name_Full
,	m.Staff_Name_Full AS Center_Manager_Name
,   v.Staff_Name_Full AS VP_Name
,	p.Staff_Name_Full AS Pathologist_Name
,	CASE WHEN m.Staff_Name_Full IS NOT NULL THEN 1 ELSE 0 END AS flag_is_center_manager
,	CASE WHEN v.Staff_Name_Full IS NOT NULL THEN 1 ELSE 0 END AS flag_is_vp
,	CASE WHEN p.Staff_Name_Full IS NOT NULL THEN 1 ELSE 0 END AS flag_is_pathologist
,	CASE WHEN lab.Staff_Lab_Technician_ID IS NOT NULL THEN 1 ELSE 0 END AS flag_lab_technician
,	CASE WHEN log.Staff_Logistical_Manager_ID IS NOT NULL THEN 1 ELSE 0 END AS flag_logistic_manager
FROM 
    STAFF s
    LEFT JOIN STAFF m ON s.Staff_Center_Manager_ID = m.Staff_ID
    LEFT JOIN STAFF v ON s.Staff_VP_Of_Centers_ID = v.Staff_ID
    LEFT JOIN STAFF p ON s.Staff_Head_Pathologist_ID = p.Staff_ID
    LEFT JOIN (SELECT DISTINCT Staff_Lab_Technician_ID FROM SAMPLE_TEST_RESULT) lab ON s.Staff_ID = lab.Staff_Lab_Technician_ID
    LEFT JOIN (SELECT DISTINCT Staff_Logistical_Manager_ID FROM SAMPLE_TEST_RESULT) log ON s.Staff_ID = log.Staff_Logistical_Manager_ID;

--test results

CREATE OR REPLACE VIEW vw_test_results 
AS
SELECT 
	st.Sample_ID
,   u.Unit_ID
,	u.Unit_Control_Number 
,	s.Sample_Control_Number
,	t.Test_Name
,	tr.Test_Result_Name
,	tr.Test_Result_Interpretation
,	st.Sample_Date_Tested
,	st.Sample_Date_Test_Resulted
,	l.Staff_Name_Full AS Lab_Technician
,	log.Staff_Name_Full AS Logistical_Manager
FROM 
	SAMPLE_TEST_RESULT st
	JOIN TEST t ON st.Test_ID = t.Test_ID
	JOIN TEST_RESULT tr ON st.Test_Result_ID = tr.Test_Result_ID
	JOIN STAFF l ON st.Staff_Lab_Technician_ID = l.Staff_ID
	JOIN STAFF log ON st.Staff_Logistical_Manager_ID = log.staff_id
	JOIN SAMPLE s ON s.Sample_ID = st.Sample_ID 
	JOIN Unit u on u.Unit_ID = s.Unit_id;
    
    --unit tracking 

CREATE OR REPLACE VIEW vw_unit_tracking 
AS
SELECT 
	u.Unit_ID
,	u.Unit_Control_Number
,	u.Unit_Donation_Date
,	u.Unit_Volume_ML_Current
,	l.Location_Name
,	li.Unit_Line_Item_Name
,	t.Unit_Type_Name
,	d.Donor_RSA_Identity_Number
,	d.Donor_Name_Full
FROM UNIT u
	JOIN LOCATION l ON u.Unit_Location_Current_ID = l.Location_ID
	JOIN UNIT_LINE_ITEM li ON u.Unit_Line_Item_ID = li.Unit_Line_Item_ID
	JOIN UNIT_TYPE t ON u.Unit_Type_ID = t.Unit_Type_ID
	JOIN DONOR d ON u.Donor_ID = d.Donor_ID 
;

